windowclass.script:
function onInit()
    local tTabs = WindowTabManager.getTabsFromWindow(self);
    local nInsertLoc = 1;
    for index,tabInfo in ipairs(tTabs) do
        if tabInfo.sName == 'notes' then
            nInsertLoc = index;
        end
        if tabInfo.sName == 'pets' then
            table.remove(tTabs, index)
            table.insert(tTabs, nInsertLoc, tabInfo);
            break;
        end
    end
    WindowTabManager.updateTabDisplay(self, tTabs);

    if super and super.onInit() then
        super.onInit();
    end
end
